import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { BoardService } from '../../services/board.service';
import { Board } from '../../models/board.model';

@Component({
    selector: 'app-boards-page',
    standalone: true,
    imports: [CommonModule, FormsModule],
    templateUrl: './boards-page.component.html'
})
export class BoardsPageComponent implements OnInit {
    boards: Board[] = [];
    newName = '';
    constructor(private service: BoardService, private router: Router) { }
    ngOnInit() { this.load(); }
    load() { this.service.getBoards().subscribe(d => this.boards = d); }
    add() { if (this.newName) this.service.createBoard(this.newName).subscribe(() => { this.newName = ''; this.load(); }); }
    open(id: number) { this.router.navigate(['/board', id]); }
    del(id: number) { this.service.deleteBoard(id).subscribe(() => this.load()); }
}
